import { useState } from 'react';

import { useWorkoutsContext } from '../hooks/useWorkoutsContext';

//date-fns
import formatDistanceToNow from "date-fns/formatDistanceToNow";

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useWorkoutsContext();
  const [title, setTitle] = useState(workout.title);
  const [reps, setReps] = useState(workout.reps);
  const [load, setLoad] = useState(workout.load);
  const [updateInput, setUpdateInput] = useState(false);

  const handleDeleteClick = async () => {
    const response = await fetch(`/api/workouts/${workout._id}`, {
      method: 'DELETE'
    });
    const data = await response.json();
    if (response.ok) {
      dispatch({ type: 'DELETE_WORKOUT', payload: data });
    }
  }
  const handleUpdateClick = async () => {
    setUpdateInput(!updateInput)
  }
  return (
    <div className="workout-details">
      <h4>{workout.title}</h4>
      <p><strong>Load Kg: </strong>{workout.load}</p>
      <p><strong>Reps: </strong>{workout.reps}</p>
      <p>{formatDistanceToNow(new Date(workout.createdAt), {addSuffix: true})}</p>
      <span className="material-symbols-outlined delete" onClick={handleDeleteClick}>delete</span>
      <span className="material-symbols-outlined update" onClick={handleUpdateClick}>settings</span>
      {updateInput && <div className="updateInput">
      <label>Exercise Title:</label>
      <input
        type="text"
        onChange={(e) => { setTitle(e.target.value) }}
        value={title}
        className={emptyFields.includes('title') ? 'error' : ''}
      />

      <label>Exercise Load (in Kg):</label>
      <input
        type="number"
        onChange={(e) => {setLoad(e.target.value)}}
        value={load}
        className={emptyFields.includes('load') ? 'error' : ''}
      />

      <label>Exercise Reps:</label>
      <input
        type="number"
        onChange={(e) => {setReps(e.target.value)}}
        value={reps}
        className={emptyFields.includes('reps') ? 'error' : ''}
      />
      <button>Add Workout</button></div>}
    </div>
  )
}

export default WorkoutDetails;
